using System;namespace P202{    class Program    {        static void Main()        {            int second, minute, hour;            Console.Write("请输入一个秒数：");            int time = Convert.ToInt32(Console.ReadLine());            if (time % 60 == 0)            {                hour = time / 3600;                Console.WriteLine("转化结果是：{0}小时", hour);            }            else            {                hour = time / 3600;                minute = time % 3600 / 60;                second = time % 3600 % 60;                Console.WriteLine("转化结果是：{0}小时{1}分钟{2}秒", hour, minute, second);            }            Console.ReadKey();        }    }}